let step_button=document.querySelectorAll(".how__step-link"),step_text=document.querySelectorAll(".how__step-slider-block-item"),step_photo=document.querySelectorAll(".how__step-slider-img");step_button.forEach(function(t){t.addEventListener("click",function(t){const e=t.currentTarget.dataset.path;step_button.forEach(function(t){t.classList.remove("how__step-link--active")}),step_text.forEach(function(t){t.classList.remove("how__step-slider-block-item--show")}),step_photo.forEach(function(t){t.classList.remove("how__step-slider-img--show")}),t.currentTarget.classList.add("how__step-link--active"),step_text.forEach(function(){document.querySelector(`[data-target="${e}"]`).classList.add("how__step-slider-block-item--show")}),step_photo.forEach(function(){document.querySelector(`[data-target="${e}img"]`).classList.add("how__step-slider-img--show")})})});
